Here is a sample implementation of the photo gallery component in Vue 3 Composition API with <script setup> using TypeScript:

```typescript
<script setup lang="ts">
import { ref, watch } from 'vue'

// Props
const props = defineProps<{
  photos: {
    thumbnail: string
    fullsize: string
    title: string
    description: string
  }[]
  slideshowInterval?: number
}>()

// Component state
const currentPhotoIndex = ref(0)
const isSlideshowActive = ref(false)

// Methods
function openLightbox(index: number) {
  currentPhotoIndex.value = index
} 

function closeLightbox() {
  currentPhotoIndex.value = -1
}

function nextPhoto() {
  currentPhotoIndex.value = 
    (currentPhotoIndex.value + 1) % props.photos.length
}

function prevPhoto() {
  currentPhotoIndex.value = 
    (currentPhotoIndex.value - 1 + props.photos.length) % props.photos.length
}

function startSlideshow() {
  isSlideshowActive.value = true

  const interval = props.slideshowInterval || 3000
  setInterval(() => {
    nextPhoto()
  }, interval)
}

function stopSlideshow() {
  isSlideshowActive.value = false
}

// Watchers
watch(isSlideshowActive, newValue => {
  if (!newValue) {
    stopSlideshow() 
  }
})

</script>

<template>

  <div>

    <button @click="startSlideshow">
      Start Slideshow
    </button>

    <div class="thumbnails">
      <img 
        v-for="(photo, index) in props.photos" 
        :key="index"
        :src="photo.thumbnail"
        :title="photo.title" 
        @click="openLightbox(index)"
      >
    </div>

    <Teleport to="body">

      <div 
        v-if="currentPhotoIndex >= 0" 
        class="lightbox"
      >

        <button @click="closeLightbox">
          Close
        </button>

        <button @click="prevPhoto">
          Prev
        </button>

        <img 
          :src="props.photos[currentPhotoIndex].fullsize"
          class="lightbox-image" 
        >

        <button @click="nextPhoto">
          Next 
        </button>

        <div class="lightbox-info">
          <strong>{{ props.photos[currentPhotoIndex].title }}</strong>
          <p>{{ props.photos[currentPhotoIndex].description }}</p>
        </div>

        <button 
          @click="isSlideshowActive ? stopSlideshow() : startSlideshow()"
        >
          {{ isSlideshowActive ? 'Pause' : 'Play' }}
        </button>

      </div>

    </Teleport>

  </div>

</template>

<style>
/* Lightbox styles */
</style>
