function parseArrayBuffer(arrayBuffer) {

  // Convert array buffer to Uint8Array for easier parsing
  const buffer = new Uint8Array(arrayBuffer);

  // Parse header
  const magic = new TextDecoder().decode(buffer.subarray(0, 4));
  const version = buffer.getUint32(4); 
  const dataSize = buffer.getUint32(8);

  // Validate header
  if (magic !== 'SIGN' || version !== 1) {
    return {error: 'Invalid header'}; 
  }

  // Parse data
  let offset = 12; // Start after header
  const data = [];

  while (offset < dataSize) {
    const strLen = buffer.indexOf(0, offset); // Find null byte
    if (strLen === -1) {
      // No null byte found but we're not at the end yet
      return {error: 'Unexpected end of data'};
    }

    const str = new TextDecoder().decode(buffer.subarray(offset, strLen));
    data.push(str);

    offset = strLen + 1; // Move past null byte
  }

  // Return parsed data
  return {parsed_object: data};

}
